int Look_Ad()
{
	
	//std::cout << "\n" << "x,y,z of set: " << x_g << "/" << y_g << "/" << z_g << "\n";
	
	float x, y, z,seite = 0;
	for (float L_strahl = 0; L_strahl < 5; L_strahl += 0.1)//L_strahl = länge des strahles
	{
		x = (L_strahl * lx) + x_g;
		y = (L_strahl * ly) + y_g;
		z = (L_strahl * lz) + z_g;
		
		//std::cout /*<< "\n" << "x,y,z mit of set" << x << "/" << y << "/" << z << " \n" */<< "x,y,z one of set" << (x - x_g) << "/" << (y - y_g) << "/" << (z - z_g) << "\n";
		//nachkomerstelen ein grenzen (di_Zero diwison durch null)
		/*x = (int)(x * 10);
		y = (int)(y * 10);
		z = (int)(z * 10);*/


		if (0 < World[(int)(x + 0)][(int)(y + 0)][(int)(z + 0)])
		{
			//printf("x,y,z one of set: %5f / %5f / %5f \n", x, y , z );
			
					
					
			if((((int)z + 0.1) >= z) && ((int)z <= z))seite = 6;
			else
			{
				if (((((int)z + 1) >= z) && (((int)z + 0.9) <= z)))seite = 5;
				else
				{
					if (((int)(y + 0.1) >= y) && ((int)y <= y))seite = 4;
					else
					{
						if (((int)(y + 1) >= y) && ((int)(y + 0.9) <= y))seite = 3;
						else
						{
							if (((int)(x + 0.1) >= x) && ((int)x <= x))seite = 2;
							else
							{
								if (((int)(x + 1) >= x) && ((int)(x + 0.9) <= x))seite = 1;


							}

						}

					}
						
				}
			}
			
			set_data[2] = x;
			set_data[3] = y;
			set_data[4] = z;
			
			set_data[1] = World[(int)x][(int)y][(int)z];
			L_strahl = 9999;
		}
	}
	
	
	set_data[0] = seite;
	
	
	//std::cout <<  "ende \n\n\n";
	return seite;
}


