void renderScene(void) {
	// Clear Color and Depth Buffers
	glClear(GL_COLOR_BUFFER_BIT | GL_DEPTH_BUFFER_BIT);
	//glDisable(GL_TEXTURE_2D);
	glEnable(GL_TEXTURE_2D);
	// Reset transformations
	glLoadIdentity();

	glClearColor(0.3, 0.8, 1, 0);
	// Set the camera
	if (Stop == 0) {
		glutWarpPointer(mitte_fenster[0] / 2, mitte_fenster[1] / 2);
		m_x_o = m_x - mitte_fenster[0] / 2;
		m_y_o = m_y - mitte_fenster[1] / 2;
		//printf("m_y:%d ", m_y_o);
		//printf(" m_x:%d\n", m_x_o);

		angle += (float)(m_x_o / 150.0);
		lx = (sin(angle));
		lz = (-cos(angle));

		ly -= (m_y_o / 300.0);


	}
	gluLookAt(x_g, y_g, z_g,
		x_g + lx, y_g + ly, z_g + lz,
		0.0f, 1.0f, 0.0f);

	
	//World[(int)(x_g + (sin(angle)))][(int)(y_g + ly)][(int)(z_g + -cos(angle))] = 2;

	
	
	// Draw ground
	glColor3f(0.8f, 1.0f, 0.8f);
	glBegin(GL_QUADS);
	glVertex3f(-10.0f, -0.01f, -10.0f);
	glVertex3f(-10.0f, -0.01f, 10.0f);
	glVertex3f(10.0f, -0.01f, 10.0f);
	glVertex3f(10.0f, -0.01f, -10.0f);
	glEnd();
	//printf("x_g = %d :: y_g = %d :: z_g = %d\n", (int)x_g, (int)y_g, (int)z_g);
	Sleep(1);
	for (int y = ((int)y_g - sicht);((y <((int)y_g + sicht)) && (y < world_y_b)); y++)
	{
		for (int x = ((int)x_g - sicht);((x <((int)x_g + sicht)) && (x < world_x_b)); x++)
		{
			for (int z = ((int)z_g - sicht);((z <((int)z_g + sicht)) && (z < world_z_b)); z++) {
				if ((((x > -1)&&(y > -1))&&(z > -1)))
				{
					glPushMatrix();
					glTranslatef(x, y, z);
					//setblock(World[x][y][z]);
					cube_texAlle(World[x][y][z],x,y,z);
					glPopMatrix();
				}
			}
		}
	}
	glutSwapBuffers();
}

