void processSpecialKeys(int key, int xx, int yy) {

	
	float fraction_turn = 0.2f;
	//printf("key = %d:_2\n", key);
	switch (key) {

	case GLUT_KEY_LEFT:
		angle -= spet_turn;
		lx = sin(angle);
		lz = -cos(angle);
		break;
	case GLUT_KEY_RIGHT:
		angle += spet_turn;
		lx = sin(angle);
		lz = -cos(angle);
		break;
	case GLUT_KEY_UP:
		ly += spet_turn;
		break;
	case GLUT_KEY_DOWN:
		ly -= spet_turn;
		break;
	
	case GLUT_KEY_F1:
		Save_Game(name2_w, 100, world_y_b, 100,inventar.items);
		break;
	
	
	}
}

void processNormilKeys(unsigned char key,int xx, int yy)
{
	if (command.eingabe == 0) {
		c = 0;
		
		switch (key) {

		case 'w':
			x_g += lx * spet;
			z_g += lz * spet;
			if ((World[(int)x_g][(int)y_g][(int)z_g] > 0) || ((World[(int)(x_g + 0.5)][(int)(y_g + 0.5)][(int)(z_g + 0.5)] > 0) || (World[(int)(x_g - 0.5)][(int)(y_g - 0.5)][(int)(z_g - 0.5)] > 0))) {
				x_g -= lx * spet;
				z_g -= lz * spet;
			}
			break;

		case 's':
			x_g -= lx * spet;
			z_g -= lz * spet;
			if ((World[(int)x_g][(int)y_g][(int)z_g] > 0) || ((World[(int)(x_g + 0.5)][(int)(y_g + 0.5)][(int)(z_g + 0.5)] > 0) || (World[(int)(x_g - 0.5)][(int)(y_g - 0.5)][(int)(z_g - 1)] > 0))) {
				x_g += lx * spet;
				z_g += lz * spet;
			}
			break;

		case 'a':
			if (Stop == 0)Stop = 1;
			else Stop = 0;
			break;

		case 'd':
			x_g -= lx * spet;
			z_g += lz * spet;
			break;

		case ' ':
			y_g += spet;
			if (World[(int)x_g][(int)y_g][(int)z_g] > 0)y_g -= spet;
			break;
		case 'y':
			y_g -= spet;
			if (World[(int)x_g][(int)y_g][(int)z_g] > 0)y_g += spet;
			else if (World[(int)x_g][(((int)y_g) - 1)][(int)z_g] > 0)y_g += spet;
			break;

		case 27:
			exit(0);
			break;
		case 't':
			command.eingabe = 1;
			break;
		case 'e':
			print_inventar(inventar.item_slot);
			break;
		}
	}
	else
	{
		if ((key == 27) || (key == '\n')) {
			command.eingabe = 0;
			c = 0;
			std::cout << "\n";
			command_ein();
		}
		else
		{


			if (c < 20) {
				command.test[c] = key;
				std::cout << key;
				c++;
			}
			else
			{
				command.eingabe = 0;
				c = 0;
				std::cout << "\n";
			}
		}
	}
}

