void OnMouseClick(int button, int state, int x, int y)
{
	//std::cout << "button = " << button <<"\n";
	int id_block_1;
	switch (button) {
	case 0://ligs
		if (state == 0) { 
			id_block_1 = Look_Ade(0);
			if((id_block_1 > -1) && (id_block_1 < 20))inventar.items[id_block_1]++;
			//else printf("error inventar id (if((id_block_1 > -1) && (id_block_1 < 20))inventar.items[id_block_1]++;)");
			print_inventar(inventar.item_slot);
		}
		
		break;
	case 2://rast
		if (state == 0) {
			Look_Ad();
			if (inventar.items[inventar.item_slot] > 0) {
				inventar.items[inventar.item_slot]--;
				switch (set_data[0])
				{
				case 1:
					World[set_data[2] - 1][set_data[3]][set_data[4]] = inventar.item_slot;
					break;
				case 2:
					World[set_data[2] + 1][set_data[3]][set_data[4]] = inventar.item_slot;
					break;
				case 3:
					World[set_data[2]][set_data[3] - 1][set_data[4]] = inventar.item_slot;
					break;
				case 4:
					World[set_data[2]][set_data[3] + 1][set_data[4]] = inventar.item_slot;
					break;
				case 5:
					World[set_data[2]][set_data[3]][set_data[4] + 1] = inventar.item_slot;
					break;
				case 6:
					World[set_data[2]][set_data[3]][set_data[4] - 1] = inventar.item_slot;
					break;

				default:
					break;
				}
			}
			print_inventar(inventar.item_slot);
		}

		break;

	case 1://mit
		break;

	case 3://mit hoch
		if (state == 0) {
			if (inventar.item_slot > 0)inventar.item_slot--;
			print_inventar(inventar.item_slot);
		}
		break;
	
	case 4://mit runter
		if (state == 0) {
			if (inventar.item_slot < 21)inventar.item_slot++;
			print_inventar(inventar.item_slot);
		}
		break;
	}
}


